class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delComment",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();console.log("Inside Create Comment"),$.ajax({type:"post",url:"/comment/save",data:$(this).serialize(),success:function(n){console.log("Inside Create Comment ",n);let o=t.newCommentDom(n.comment);$(`#comment-${e}`).prepend(o),t.deleteComment($(" .delComment",o)),console.log("Comment Added"),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return`<li id="${e._id}">\n    <p >\n        <div class="horizontalStyle"> \n          <div class="inlineStyle">\n         \n            <a href="/like/toggle/?id=${e._id}&type=Comment" class="delLike" data-like="0"><i class="fas fa-regular fa-thumbs-up"> 0\n            </i>\n            </a> \n            \n          </div>\n         \n          <div  class="inlineStyle">\n              ${e.content}\n          </div>\n        </div>\n\n       <small>\n        ${e.user.name}\n       </small>\n    </p>\n      \n        <a class='fas fa-trash delComment' href="/comment/destroy/${e._id}"></a> \n    \n    </li>`}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){console.log("Inside delete Comment ",e),$(`#${e.comment}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}